Get-ADUser -Filter {Enabled -eq $True -and PasswordNeverExpires -eq $False} -Properties DisplayName, EmailAddress, "msDS-UserPasswordExpiryTimeComputed" | Select-Object -Property "Displayname","EmailAddress",@{Name="ExpiryDate";Expression={[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed")}} | Sort-Object EXPIRYDATE  | Export-CSV "ENTER PATH HERE EX: C:\Scripts"